import React, { useEffect, useState } from 'react';
import './MediaDisplay.css';


const DisplayMedia = ({ url }) => {

  const mediaContainerRef = useRef(null);

  useEffect(() => {
    if (!url) {
      console.error("URL or container ID is missing.");
      return;
    }

    const projectVideoContainer = mediaContainerRef.current;

    // Clear the previous content and reset styles
    projectVideoContainer.classList.remove('loaded');
    projectVideoContainer.innerHTML = ''; 

    if (url.includes('youtube.com') || url.includes('youtu.be')) {
      const youtubeId = extractYouTubeId(url);
      if (youtubeId) {
        const iframeElement = document.createElement('iframe');
        iframeElement.width = '100%';
        iframeElement.height = '100%';
        iframeElement.src = `https://www.youtube.com/embed/${youtubeId}?autoplay=1&loop=1&mute=1&controls=0&modestbranding=1&rel=0&iv_load_policy=3&showinfo=0&fs=0&disablekb=1`;
        iframeElement.setAttribute('allowfullscreen', 'true');
        iframeElement.setAttribute('frameborder', '0');
        iframeElement.setAttribute('allow', 'autoplay; encrypted-media;');
        projectVideoContainer.appendChild(iframeElement);
      }
    } else if (url.endsWith('.mp4') || url.endsWith('.webm')) {
      const videoElement = document.createElement('video');
      videoElement.setAttribute('autoplay', 'true');
      videoElement.setAttribute('loop', 'true');
      videoElement.setAttribute('muted', 'true');
      videoElement.setAttribute('controls', 'true');
      videoElement.muted = true;

      videoElement.onloadedmetadata = function() {
        if (videoElement.videoHeight > videoElement.videoWidth) {
          // Portrait video
          videoElement.style.height = `${videoElement.videoHeight / 2}px`;
          videoElement.style.width = '100%';
        } else {
          // Landscape or square video
          videoElement.style.height = '100%';
          videoElement.style.width = '100%';
        }
      };

      videoElement.src = url;
      projectVideoContainer.appendChild(videoElement);
    } else {
      const imageElement = document.createElement('img');
      imageElement.src = url;

      if (id === 'hero-container') {
        imageElement.style.width = '100%';
        imageElement.style.height = '55vh';
        imageElement.style.objectFit = 'cover';
      } else {
        imageElement.style.width = '100%';
        imageElement.style.height = 'auto';
        imageElement.style.objectFit = 'cover';
      }

      projectVideoContainer.appendChild(imageElement);
    }

    // Set the container as loaded once content is appended
    projectVideoContainer.classList.add('loaded');

  }, [url]);

  return (
    <div 
      ref={mediaContainerRef} 
      className="media-container loaded"
    />
  );
};

// Function to extract YouTube video ID from a URL
function extractYouTubeId(url) {
  const regex = /(?:youtube\.com\/(?:[^\/]+\/[^\/]+\/|(?:v|e(?:mbed)?)\/|.*[?&]v=)|youtu\.be\/)([^"&?\/\s]{11})/;
  const match = url.match(regex);
  return match ? match[1] : null;
}



export default DisplayMedia;
