import React from 'react';
import './Card.css';

const Card = ({ delay,year,tag,title, description, imageURL, caseStudyLink, learnMoreLink }) => {
    const [isLoaded, setIsLoaded] = useState(false);
  useEffect(() => {
    const timer = setTimeout(() => setIsLoaded(true), delay); // Simulate loading
    return () => clearTimeout(timer); // Cleanup timer on unmount
  }, [delay]);
  return (
    <div className="card-container">
      {/* Header Row */}
      <div className="card-header">
       <div className="card-overlay">
          <div className="card-year">{year}</div>
          <div className="card-tag">{tag}</div>
        </div>
        <img className="card-image" src={imageURL} alt={title} />
      </div>

      {/* Content Row */}
      <div className="card-content">
        <h3 className="card-title">{title}</h3>
         <p className="card-description">{description}</p>
        <div className="card-buttons">
          <a href={caseStudyLink} className="card-button card-button-case-study" target="_blank" rel="noopener noreferrer">
            Case Study
          </a>
          <a href={learnMoreLink} className="card-button card-button-learn-more" target="_blank" rel="noopener noreferrer">
            Learn More
          </a>
        </div>
      </div>
    </div>
  );
};

export default Card;
