import React, { useState, useEffect } from "react";
import './Card.css';


const Card = ({ delay,year,tag,title, description, imageURL, caseStudyLink, learnMoreLink }) => {
 const [isLoaded, setIsLoaded] = useState(false);


  useEffect(() => {
        console.log(`Card "${title}" is in the loading state.`); // Log when loading starts


    const timer = setTimeout(() => {
      setIsLoaded(true);
      console.log(`Card "${title}" has transitioned to the loaded state.`); // Log when loaded
    }, delay);

    return () => clearTimeout(timer); // Cleanup timer on unmount
  }, [delay]);
  return (
    <div className={`card-container ${isLoaded ? "loaded" : "loading"}`}
       onClick={onClick}>
       <div className="card-overlay">
          <div className="card-year">{year}</div>
          <div className="card-tag">{tag}</div>
        </div>
        <div className="card-image-container">
          <img className="card-image" src={imageURL} alt={title} />
          <h3 className="card-title">{title}</h3>
        </div>
    </div>
  );
};

export default Card;
