import React, { useEffect, useState } from 'react';
import { supabaseClient } from './supabaseClient';
import HeaderContent from './components/Header/Header'; // Import the component
import './App.css';
import HomePage from './components/Homepage/Homepage';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

function App() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true); 

  // Fetch projects from Supabase
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const { data, error } = await supabaseClient
          .from('work_v2')
          .select('*'); 

        if (error) {
          console.error('Error fetching projects:', error);
          setLoading(false);
          return;
        }

        const filteredData = data.filter((project) => project.enabled !== false);
        const sortedData = filteredData.sort((a, b) => a.order - b.order);

        console.log('Projects loaded:', sortedData); 
        setProjects(sortedData);
      } catch (error) {
        console.error('Unexpected error:', error);
      } finally {
        setLoading(false); 
      }
    };

    fetchProjects();
  }, []);

  return (
    <Router>
      <div className="app-container">
        <header className="app-header">
          <HeaderContent />
        </header>

        <main className="app-main">
          <Routes>
            <Route 
              path="/" 
              element={<HomePage projects={projects} loading={loading} />} 
            />

            <Route 
              path="/project/:projectId" 
              element={<ProjectPage />} 
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
