import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomePage from './/components/Homepage/Homepage';
import ProjectPage from './/components/Page/Page';

// Initialize Supabase client
const supabase = createClient('https://your-supabase-url.supabase.co', 'your-supabase-anon-key');

function App() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state to display loading indicator

  // Fetch projects from Supabase
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const { data, error } = await supabase
          .from('projects')
          .select('*'); // Select all columns, customize as needed

        if (error) {
          console.error('Error fetching projects:', error);
        } else {
          setProjects(data);
        }
      } catch (error) {
        console.error('Unexpected error:', error);
      } finally {
        setLoading(false); // End loading state
      }
    };

    fetchProjects();
  }, []);

  return (
    <Router>
      <div className="app-container">
        <header className="app-header">
          <HeaderContent />
        </header>

        <main className="app-main">
          {loading && <div className="loading-indicator">Loading projects...</div>}

          <Routes>
            {/* Home Page Route */}
            <Route 
              path="/" 
              element={<HomePage projects={projects} loading={loading} />} 
            />

            {/* Single Project Page Route */}
            <Route 
              path="/project/:projectId" 
              element={<ProjectPage />} 
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
