import React, { useEffect, useState } from 'react';
import { supabaseClient } from './supabaseClient';
import Card from './components/Card/Card';
import './App.css';

function App() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true); // New state for loading

  // Fetch projects from Supabase
  useEffect(() => {
    const fetchProjects = async () => {
      const { data, error } = await supabaseClient
        .from('work_v3')
        .select('id, tag, title, description, case_study, imageURL, order, date');

      if (error) {
        console.error('Error fetching projects:', error);
        setLoading(false); // Stop loading on error
        return;
      }

      // Filter and sort projects
      const filteredData = data.filter((project) => project.enabled !== false);
      const sortedData = filteredData.sort((a, b) => a.order - b.order);

      setProjects(sortedData);
      setLoading(false); // Stop loading once data is fetched
    };

    fetchProjects();
  }, []);

  return (
    <div className="app-container">
      <header className="app-header">
        <div className="header-intro">Hi! I’m Charles Leclercq. I’m passionate about Storytelling, Innovation, and HCI.</div>
        <div className="header-nav">Navigation</div>
        <div className="header-actions">Actions</div>
      </header>
      <main className="app-main">
        {/* Loading or Loaded Message */}
        {loading ? (
          <p>Loading projects...</p> // Show loading message
        ) : projects.length > 0 ? (
          <p>Projects have loaded!</p> // Show confirmation after load
        ) : (
          <p>No projects available.</p> // Handle case of no projects
        )}

        {/* Project Grid */}
        <div className="project-grid">
          {projects.map((project, index) => (
            <Card
              key={project.id}
              year={project.date}
              tag={project.tag}
              title={project.title}
              description={project.description}
              imageURL={project.imageURL}
              caseStudyLink={`/case-study/${project.id}`}
              learnMoreLink={`/learn-more/${project.id}`}
              delay={300 * (1 - Math.exp(-index / 5))}
            />
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
