import React, { useState } from 'react';
import Card from '../components/Card';
import Page from '../components/Page';
import HeaderContent from '../components/HeaderContent';

const HomePage = ({ projects }) => {
  const [projectStatuses, setProjectStatuses] = useState(
    projects.reduce((acc, project) => ({ ...acc, [project.id]: false }), {})
  );

  const toggleProjectStatus = (projectId) => {
    setProjectStatuses((prevStatuses) => ({
      ...prevStatuses,
      [projectId]: !prevStatuses[projectId]
    }));
  };

  const closeAllProjects = () => {
    setProjectStatuses(projects.reduce((acc, project) => ({ ...acc, [project.id]: false }), {}));
  };

  return (
    <div className="app-container">
      <HeaderContent />
      <button className="close-all-button" onClick={closeAllProjects}>Close All</button>
      <div className="project-grid">
        {projects.map((project) => (
          <Card 
            key={project.id} 
            project={project} 
            status={projectStatuses[project.id] ? 'loaded' : 'unloaded'}
            onClick={() => toggleProjectStatus(project.id)} 
          />
        ))}
      </div>
      {Object.entries(projectStatuses).map(([projectId, isLoaded]) => (
        isLoaded && <Page key={projectId} />
      ))}
    </div>
  );
};

export default HomePage;
