import React, { useEffect, useState } from 'react';
import { supabaseClient } from './supabaseClient';  // Import Supabase client

function App() {
  const [projects, setProjects] = useState([]);

  // Fetch data from Supabase
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        // Fetch project data including the 'order' property
      const { data: projectsData, error: projectsError } = await supabaseClient
        .from('work')
        .select('id, title, imageURL, url2, url3, enabled, order, role, projectdate,selection_title, company, industry, what, why, tech');


        if (projectsError) {
          console.error('Error fetching projects data:', projectsError);
          return;
        }

        // Filter out disabled projects
        const filteredData = projectsData.filter(project => project.enabled !== false);

        // Sort projects by 'order'
        const sortedData = filteredData.sort((a, b) => a.order - b.order);
        setProjects(sortedData);

        // Set the first project by default
        if (sortedData.length > 0) {
          setSelectedProject(sortedData[0]);
          setCurrentMediaIndex(0);  // Start with the first media
        }

        // Fetch a default preview image
        const { data: imagesData, error: imagesError } = await supabaseClient
          .from('work')
          .select('imageURL')
          .limit(1);

        if (imagesError) {
          console.error('Error fetching preview image:', imagesError);
        } else if (imagesData.length > 0) {
          setDefaultPreview(imagesData[0].imageURL);  // Set the first image as the default preview
        }

      } catch (err) {
        console.error('Unexpected error:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="App">
      <h1>My Portfolio</h1>
      <div className="project-grid">
        {projects.map((project) => (
          <div key={project.id} className="project-card">
            <img
              src={project.hero_image}
              alt={project.title}
              className="project-image"
            />
            <h2>{project.title}</h2>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
