import React, { useEffect, useState } from 'react';
import { supabaseClient } from './supabaseClient';
import Card from './components/Card/Card';
import './App.css';

function App() {
  const [projects, setProjects] = useState([]);

  // Fetch projects from Supabase
  useEffect(() => {
    const fetchProjects = async () => {
      const { data, error } = await supabaseClient
        .from('work')
        .select('id, tag, title, imageURL, url2, url3, enabled, order, role, projectdate,selection_title, company, industry, what, why, tech');


      if (error) {
        console.error('Error fetching projects:', error);
        return;
      }

      // Filter and sort projects
      const filteredData = data.filter((project) => project.enabled !== false);
      const sortedData = filteredData.sort((a, b) => a.order - b.order);


      for(let i = 0; i<sortedData.length; i++){
        setTimeout(() => {
            setProjects(sortedData[i]);
          }, 500); // Simulate a delay for fetching
      }

      // setProjects(sortedData);
    };

    fetchProjects();
  }, []);

 return (
    <div className="app-container">
      <header className="app-header">
        <div class="header-intro">Hi!I’m Charles Leclercq.I’m passionate about Storytelling, Innovation, and HCI.</div>
        <div class="header-nav">Navigation</div>
        <div class="header-actions">Actions</div>
      </header>
      <main className="app-main">
        <div className="project-grid">
          {projects.map((project) => (
            <Card
              key={project.id}
              year={project.projectdate}
              tag={project.tag}
              title={project.title}
              description={project.why}
              imageURL={project.imageURL}
              caseStudyLink={`/case-study/${project.id}`}
              learnMoreLink={`/learn-more/${project.id}`}
            />
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
